import axios from "axios";
import { getAllProducts, getProductById } from "./productSlice";

// export const getProducts = async (dispatch) => {
//     await axios("http://localhost:3001/pokemon/get/").then(res => dispatch(getAllProducts(res.data.results))).catch(error => console.log(error))
// }

export const getProducts = () => {
    return async (dispatch) => {
        const dataApi = await axios.get("http://localhost:3001/pokemon/get/");
        const products = dataApi.data;
        dispatch(getAllProducts(products));
    };
};
