import React from "react";
import { Link } from 'react-router-dom'

const Products = (_id, nombre, foto, precio) => {

	return (
		<div className='content'>
				<Link to={'/product/' + _id}>
				<img className='card_image' src={foto} alt='img not found'/>
				</Link>
				<h3>{nombre}</h3>
				<p>	
					$ {precio}
				</p>
		</div>
);
};


export default { 
	Products 
};



