import { useDispatch } from "react-redux";
import { useState } from "react";
import { NavLink, useLocation } from "react-router-dom";
import { getAllProductsByName } from "../../../redux/productActions";

//NOTA >>>> LO QUE ESTA COMENTADO TOCA ESPERAR A QUE ESTE LISTO EL REDUCER PARA QUE FUNCIONE

const SearchBar = () => {
    const dispatch = useDispatch()
    const [ name, setName ] = useState("")
    const locationObject = useLocation()
    const location = locationObject.pathname  

    const handleChange = (event) => {
        setName(event.target.value)
    }

    const onSearch = (name) => {
        const urlBack = `${location}?name`
        if(name) dispatch(getAllProductsByName(name))                //ESPERAR HACER EL REDUCER PARA QUE FUNCIONE
    }


    return (
        <div>
            <input type="search" onChange={handleChange} value={name} placeholder="Buscar productos" />
            <NavLink to={"/"}>
                <button onClick={() => { onSearch(name); setName("")}}>üîç</button>
            </NavLink>
        </div>
    )

};

export default SearchBar;
