import React from "react";
import user from './user.jpg'
const Product = () => {
  const base = [
    {
      id: 0,
      nombre: "juan",
      descripcion: "Description",
      precio: 123,
      imagen: { user },
      stock: 100,
      categoria: "Category A",
      categoria_id: 1,
    },
    {
      id: 1,
      nombre: "pedro",
      descripcion: "Description",
      precio: 456,
      imagen: { user },
      stock: 34,
      categoria: "Category B",
    },
    {
      id: 2,
      nombre: "dante",
      descripcion: "Description",
      precio: 87,
      imagen: { user },
      stock: 34,
      categoria: "Category C",
    },
    {
      id: 3,
      nombre: "Name",
      descripcion: "Description",
      precio: 554,
      imagen: { user },
      stock: 1,
      categoria: "Category D",
    },
    {
      id: 4,
      nombre: "tadeo",
      descripcion: "Description",
      precio: 422,
      imagen: { user },
      stock: 22,
      categoria: "Category E",
    },
    {
      id: 5,
      nombre: "laura",
      descripcion: "Description",
      precio: 111,
      imagen: { user },
      stock: 22,
      categoria: "Category F",
    },
  ];

  return (
    <div className="container">
      <div className="row">
        {base.map((base, index) => {
          return (
            <div key={index} className="col-md-4">
              <div className="card">
                <img className="card-img-top" src={base.imagen} alt="Card image cap"/>
                <div className="card-body">
									<h5 className="card-title">{base.nombre}</h5>
                	<p className="card-text">$ {base.precio}</p>
								</div>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default Product;
