import React from "react";

const Products = (_id, nombre, foto, precio) => {
  return (
    <div className="card">
      <img className="card_image" src={foto} alt="img not found" />
      <div className="card-body">
        <h3 className="card-title">{nombre}</h3>
        <h4 className="card-text">$ {precio}</h4>
      </div>
    </div>
  );
}; 

export default {
  Products,
};

