import React from "react";

const About = () => {
  const localAddress = "Dirección del local"; // Inserta aquí la dirección del local

  const openGoogleMaps = () => {
    const mapUrl = `https://www.google.com/maps/search/?api=1&query=${encodeURIComponent(
      localAddress
    )}`;
    window.open(mapUrl, "_blank");
  };

  return (
    <div className="container">
      <div className="row">
        <div className="col-md-6">
          <h2>Formulario de contacto</h2>
          <form>
            {/* ... */}
          </form>
        </div>
        <div className="col-md-6">
          <h2>Información de ubicación</h2>
          <p>Dirección: {localAddress}</p>
          <p>Teléfono: Número de teléfono</p>
          <ul className="list-inline">
            <li className="list-inline-item">
              <a href="enlace-a-red-social-1" className="text-decoration-none">
                {/* ... */}
              </a>
            </li>
            <li className="list-inline-item">
              <a href="enlace-a-red-social-2" className="text-decoration-none">
                {/* ... */}
              </a>
            </li>
          </ul>
          <div className="mt-4">
            <img src="ruta-a-la-imagen-de-vista-previa-de-google-maps" alt="Vista previa de Google Maps" className="img-fluid" />
            <div className="d-flex align-items-center mt-2">
              <i className="bi bi-geo-alt text-success me-2" style={{ cursor: "pointer" }} onClick={openGoogleMaps}></i>
              <span className="text-success cursor-pointer" onClick={openGoogleMaps}>Google Maps</span>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default About;
