import React from "react";


const Product = ({product}) => {
	
  return (
    <div className="card">
     <img className="card_image" src={product.foto} alt="img not found" />
      <div className="card-body">
        <h3 className="card-title">{product.nombre}</h3>
        <h4 className="card-text text-secondary">$ {product.precio}</h4>
      </div>
    </div>
  );
}; 

export default Product;
