import React, { useEffect } from "react";
import { useDispatch, useSelector } from 'react-redux';
import { useParams, Link } from "react-router-dom";

const Detail = () => {
  const { id } = useParams();
  const dispatch = useDispatch();
  const { products } = useSelector(state => state.products);
  const product = products.find(item => item.id === id);

  useEffect(() => {
    // Aquí puedes agregar la lógica para obtener los detalles del producto según su id
  }, [id, dispatch]);

  if (!product) {
    return <div>Producto no encontrado</div>;
  }

  return (
    <div className="detail-container">

      <div className="detail-titles">
        <h1 id="detailH1">{product.name}</h1>

        <div className="detailH1">
          <h2 className="detailH2">Marca: {product.brand}</h2>
          <h2 className="detailH2">Stock: {product.stock}</h2>
          <h2 className="detailH2">Rating: {product.rating}</h2>
          <h2 className="detailH2">Descripción: {product.description}</h2>
          <h2 className="detailH2">Precio: ${product.price}</h2>
          <button className="btn btn-primary">Comprar</button>
        </div>
      </div>

      <div className="image-container">
        <img className="imgDetail" src={product.image} alt={product.name} />
      </div>
    </div>
  );
};

export default Detail;
